import type { Document } from 'bson';

import type { Collection } from 'mongodb/src/collection';
import type { Server } from 'mongodb/src/sdam/server';
import type { ClientSession } from 'mongodb/src/sessions';
import type { Callback } from 'mongodb/src/utils';
import { AbstractCallbackOperation } from 'mongodb/src/operations/operation';

/** @internal */
export class DropSearchIndexOperation extends AbstractCallbackOperation<void> {
  constructor(private readonly collection: Collection, private readonly name: string) {
    super();
  }

  executeCallback(
    server: Server,
    session: ClientSession | undefined,
    callback: Callback<void>
  ): void {
    const namespace = this.collection.fullNamespace;

    const command: Document = {
      dropSearchIndex: namespace.collection
    };

    if (typeof this.name === 'string') {
      command.name = this.name;
    }

    server.command(namespace, command, { session }, err => {
      if (err) {
        callback(err);
        return;
      }

      callback();
    });
  }
}
