import React, {useState , useEffect , useReducer} from 'react';
import {formReducer,INITIAL_STATE} from './InitialState';
import {type} from "@testing-library/user-event/dist/type";

const nameReducer=(state, action) =>{
    if(action.type === 'USER_INPUT') {
        return {value:action.val,isValid:action.val.trim().length >= 3};
    }
    if(action.type === 'INPUT_BLUR') {
        return {value:state.value,isValid:state.value.trim().length > 6}
    }
    return {value:'',isValid:false}

}
const surnameReducer=(state, action) =>{
    if(action.type === 'USER_INPUT') {
        return {value:action.val,isValid:action.val.trim().length > 3};
    }
    if(action.type === 'INPUT_BLUR') {
        return {value:state.value,isValid:state.value.trim().length > 6}
    }
    return {value:'',isValid:false}

}
const emailReducer=(state,action) => {
    if(action.type === 'USER_INPUT') {
        return {value:action.val,isValid:action.val.includes('@')};
    }
    if(action.type === 'INPUT_BLUR') {
        return {value:state.value,isValid:state.value.includes('@')}
    }
    return {value:'',isValid:false}
}

const phoneReducer=(state,action) => {
    if(action.type === 'USER_INPUT') {
        return {value:action.val,isValid:action.val.trim().length > 9};
    }
    if(action.type === 'INPUT_BLUR') {
        return {value:state.value,isValid:state.value.trim().length > 9}
    }
    return {value:'',isValid:false}
}
const addressReducer=(state,action) => {
    if(action.type === 'USER_INPUT') {
        return {value:action.val,isValid:action.val.trim().length > 25};
    }
    if(action.type === 'INPUT_BLUR') {
        return {value:state.value,isValid:state.value.trim().length >25 }
    }
    return {value:'',isValid:false}
}
const passwordReducer=(state,action) => {
    if(action.type === 'USER_INPUT') {
        return {value:action.val,isValid:action.val.trim().length > 6}
    }
    if(action.type === 'INPUT_BLUR') {
        return {value:state.value,isValid:state.val.trim().length > 6}
    }
    return {value:'',isValid:false}

}

export function RegisterPage(){

    const [nameState,dispatchName] = useReducer(nameReducer, {value:'',isValid:null});
    const [surNameState,dispatchSurName] = useReducer(surnameReducer, {value:'',isValid:null});
    const [emailState,dispatchEmail] = useReducer(emailReducer, {value:'',isValid:null});
    const [phoneState,dispatchPhone] = useReducer(phoneReducer, {value:'',isValid:null});
    const [addressState,dispatchAddress] = useReducer(addressReducer, {value:'',isValid:null});
    const[passwordState,dispatchPassword] = useReducer(passwordReducer,{value:'',isValid:null})

    const[state,dispatch] = useReducer(formReducer,INITIAL_STATE);

    const handleChange = (e)=>{
        dispatch({type: "CHANGE_INPUT" , val:{name:e.target.name,value:e.target.value}})
    }
    const nameChangeHandler = (event) => {
        // setEnteredEmail(event.target.value);
        dispatchName({type:'USER_INPUT',val:event.target.value});

        // setFormIsValid(
        //   event.target.value.includes('@') && passwordState.isValid
        // );
    };
    const surnameChangeHandler = (event) => {
        // setEnteredEmail(event.target.value);
        dispatchSurName({type:'USER_INPUT',val:event.target.value});

        // setFormIsValid(
        //   event.target.value.includes('@') && passwordState.isValid
        // );
    };
    const emailChangeHandler = (event) => {
        // setEnteredEmail(event.target.value);
        dispatchEmail({type:'USER_INPUT',val:event.target.value});

        // setFormIsValid(
        //   event.target.value.includes('@') && passwordState.isValid
        // );
    };
    const phoneChangeHandler = (event) => {
        // setEnteredEmail(event.target.value);
        dispatchPhone({type:'USER_INPUT',val:event.target.value});

        // setFormIsValid(
        //   event.target.value.includes('@') && passwordState.isValid
        // );
    };
    const addressChangeHandler = (event) => {
        // setEnteredEmail(event.target.value);
        dispatchAddress({type:'USER_INPUT',val:event.target.value});

        // setFormIsValid(
        //   event.target.value.includes('@') && passwordState.isValid
        // );
    };

    const passwordChangeHandler = (event) => {
        // setEnteredPassword(event.target.value);
        dispatchPassword({type:'USER_INPUT',val:event.target.value});

        // setFormIsValid(
        //   emailState.isValid && event.target.value.trim().length > 6
        // );
    };

    const resetForm = (e) => {
        emailState('');
        passwordState('');
        addressState('');
        nameState('');
        surNameState('');
        phoneState('')

    }
    const registerForm =(e) => {
        e.preventDefault();

    }

    return(

        <div className="form-element">
            <fieldset name="Register">
                <form className="login-form" onSubmit={registerForm}>
                    <div className="form_elements">
                        <label htmlFor="Name">First Name:</label>
                        <input id="Name" type="text" placeholder="First Name" name="firstname"
                               value={nameState.value} onChange={nameChangeHandler}/>
                    </div>
                    <div className="form_elements">
                        <label htmlFor="Surname">Last Name:</label>
                        <input id="Surname" type="text" placeholder="Last name" name="lastname"
                               value={surNameState.value} onChange={surnameChangeHandler}/>
                    </div>
                    <div className="form_elements">
                        <label htmlFor="email">Email:</label>
                        <input id="email" type="email" placeholder="Email" name="email"
                               value={emailState.value} onChange={emailChangeHandler}/>
                    </div>
                    <div className="form_elements">
                        <label htmlFor="phonenumber">Phone Number:</label>
                        <input id="email" type="tel" placeholder="Phone Number" name="phonenumber"
                               value={phoneState.value} onChange={phoneChangeHandler}/>
                    </div>
                    <div className="form_elements">
                        <label htmlFor="address">Address: </label>
                        <input id="address" type="text" placeholder="address" name="address"
                               value={addressState.value} onChange={addressChangeHandler}/>
                    </div>
                    <div className="form_elements">
                        <label htmlFor="password">Phone Number:</label>
                        <input id="password" type="password" placeholder="password" name="password"
                               value={passwordState.value} onChange={passwordChangeHandler}/>
                    </div>

                    <div className="buttons">
                        <button type="submit" className="button-submit">Log In</button>
                        <button type="reset" onClick={resetForm} >Reset</button>

                    </div>


                </form>

            </fieldset>
        </div>


    )

}
