import React, {useState , useEffect , useReducer} from 'react';
import {Routes,Route, useNavigate} from "react-router-dom";
import axios from 'axios';
import './Register.css'


export function RegisterPage(){

    const[firstName,setFirstName]= useState('')
    const[lastName,setLastName]= useState('')
    const[email,setEmail]= useState('')
    const[address,setAddress]= useState('')
    const[phonenumber,setPhonenumber]= useState('')
    const[password,setPassword]= useState('')
    const[error,setError]= useState('');
    const[message,setMessage]= useState('')
    const [formIsValid, setFormIsValid] = useState(true);
    const navigate = useNavigate();

    const navigatetoLogin = ()=>{
        //navigate to /login
        navigate('/login');

    }
    useEffect(() => {
        const identifier = setTimeout(() => {
            setError('');
            setMessage('')
        }, 6000);

        return () => {
            clearTimeout(identifier);
        };
    }, [firstName,lastName,email, address, phonenumber, password, error]);
    const nameChangeHandler = (event) => {
        setFirstName(event.target.value)


    };
    const validateFirstName= (event) => {

    }

    const surnameChangeHandler = (event) => {
        setLastName(event.target.value)

        setFormIsValid(!/\d/.test(event.target.value))
    };
    const emailChangeHandler = (event) => {
        setEmail(event.target.value);
        setFormIsValid(event.target.value.includes('@'))

    };
    const phoneChangeHandler = (event) => {
        setPhonenumber(event.target.value);
        // dispatchPhone({type:'USER_INPUT',val:event.target.value});

        // setFormIsValid(
        //   event.target.value.includes('@') && passwordState.isValid
        // );
    };
    const addressChangeHandler = (event) => {
        setAddress(event.target.value);
        // dispatchAddress({type:'USER_INPUT',val:event.target.value});

        // setFormIsValid(
        //   event.target.value.includes('@') && passwordState.isValid
