    };

    const resetForm = (e) => {
        setFirstName('');
        setLastName('');
        setEmail('');
        setPassword('');
        setPhonenumber('')
        setAddress('')
    }
    const postData = async ()=>{

        const postData = {
            firstName:firstName,
            lastName:lastName,
            email:email,
            phonenumber:phonenumber,
            password:password,
            address:address
        }
        await axios.post('http://localhost:4000/register-us',postData).then(response => setMessage(response)).then(navigatetoLogin).catch(error =>setError('Email in use.Please log in'))

    }


    const registerForm =(e) => {
        e.preventDefault();
        postData();
        setFirstName('');
        setLastName('');
        setEmail('');
        setPassword('');
        setPhonenumber('')
        setAddress('')
    }

    return(

        <div className="form-element">

                <form className="login-form" onSubmit={registerForm} >

                    <fieldset name="Register">
                        <legend className='register'>Register</legend>
                    <div className="form_elements">
                        <label htmlFor="Name" className='labels'>First Name:</label>
                        <input className="inputs" id="Name" type="text" placeholder="First Name" name="firstName"
                               value={firstName} onChange={nameChangeHandler}  required/>

                    </div>
                    <p  className={formIsValid ? 'valid' : 'invalid'}>Please enter valued name</p>
                    <div className="form_elements">
                        <label htmlFor="Surname" className='labels'>Last Name:</label>
                        <input className="inputs" id="Surname" type="text" placeholder="Last name" name="lastName"
                               value={lastName} onChange={surnameChangeHandler}  required/>
                    </div>
                    <div className="form_elements">
                        <label htmlFor="email" className='labels'>Email:</label>
                        <input className="inputs" id="email" type="email" placeholder="Email" name="email"
