export const INITIAL_STATE = {
    firstname:'',
    lastname:'',
    email:'',
    phonenumber:'',
    address:'',
    password:'',
    isValid:null

}

export const formReducer = (state,action) =>{

    switch(action.type){
        case "CHANGE_INPUT":
            return{...state, [action.val.name]: action.val.value,isValid:action.val.trim().length > 6

            }
        case "CHANGE_EMAIL":
            return{...state, [action.val.name]: action.val.value,isValid:action.val.includes('@')}
        case "CHANGE_PASSWORD":
            return{...state, [action.val.name]: action.val.value,isValid:action.val.trim().length > 6}
        case "CHANGE_ADDRESS":
            return{...state, [action.val.name]: action.val.value,isValid:action.val.trim().length > 50}
        case "CHANGE_PHONE":
            return{...state, [action.val.name]: action.val.value,isValid:action.val.trim().length > 9}
        default:
            return state

    }

}
