import React , {useState , useEffect} from 'react';
import { useParams } from 'react-router-dom';
import './PostTravel.css'



export function PostTravel (){

let {id} = useParams();
const[location,setLocation] = useState('')
    const[date,setDate] = useState('')
const[cost,setCost] = useState('')
const[post,setPost] = useState('')
useEffect(() => {


},[location, date, cost, post])

const locationHandler =(e) =>{
    setLocation(e.target.value)
}
    const dateHandler =(e) =>{
        setDate(e.target.value)
    }
    const costHandler =(e) =>{
        setCost(e.target.value)
    }
    const postHandler =(e) =>{
        setPost(e.target.value)
    }

return(
<>
    <section className='posting'>

        <form className="login-form" >
            <h1 className='title'>Create  Post</h1>
            <div className="form_elements">
                <i className="fa-solid fa-location-dot icons"></i>
                <input className='inputs'  type='text' placeholder='Add Location' name='location' onChange={locationHandler} />
            </div>
            <div className="form_elements">
                <i className="fa-solid fa-calendar-days icons"></i>
                <input className="inputs"  type='date' placeholder='Add date of visit' name='date' onChange={dateHandler} />
            </div>
            <div className="form_elements">
                <i className="fa-solid fa-money-check-dollar icons"></i>
                <input className="inputs"  type='number' placeholder='cost of visit' name='cost' onChange={costHandler} />
            </div>
            <div>
                <textarea  placeholder='Post something' name='post' rows="4" cols="50" onChange={postHandler} />
            </div>
            <button className='button-submit'>Post It</button>
        </form>

    </section>


</>
                )

}
